  }
  jwt.verify(token, JWT_SECRET, function (err, decoded) {
    if (err) {
      return res.status(401).json({
        message: 'Unauthorized'
      });
    }
    req.userId = decoded.id;
    next();
  });
}
module.exports = {
  verifyToken: verifyToken
